scope.todoPage:
import * as React from "react";
import { useLoader } from "@mwap/loaders";
import { useParams } from "@mwap/router";

import type { TodoPageData, TodoPageArgs } from "../loaders/todo";

const TodoPage = () => {
  const { id } = useParams<TodoPageArgs>();
  const todo = useLoader<TodoPageData, TodoPageArgs>("todo", { id });

  return (
    <pre>
      <code>{JSON.stringify(todo, null, 2)}</code>
    </pre>
  );
};

export default TodoPage;

scope.todoPageLoader:
import { fetch } from "cross-fetch";
import type { Loader } from "@mwap/loaders";

export type TodoPageData = {
  userId: number;
  id: number;
  title: string;
  completed: boolean;
};

export type TodoPageArgs = {
  id: string | number;
};

const loader: Loader<TodoPageData, TodoPageArgs> = async ({ params }) => {
  return fetch(
    `https://jsonplaceholder.typicode.com/todos/${params.id}`
  ).then((response) => response.json());
};

export default { loader };

scope.todoPages:
import type { Page } from "@mwap/app";

import Home from "./home";
import Todo from "./todo";

const pages: Page[] = [
  {
    component: Home,
    exact: true,
    path: "/",
  },
  {
    component: Todo,
    path: "/todo/:id",
  },
];

export default pages;
